<template>
  <div v-if="didError" :class="['inline-block bg-gray-100 text-center align-middle', customClass]" :style="style">
    <div class="flex items-center justify-center w-full h-full">
      <img :src="ERROR_IMG_SRC" alt="Error loading image" v-bind="restAttrs" :data-original-url="src" />
    </div>
  </div>
  <img
    v-else
    :src="src"
    :alt="alt"
    :class="customClass"
    :style="style"
    v-bind="restAttrs"
    @error="handleError"
  />
</template>

<script setup lang="ts">
import { ref, computed, useAttrs } from 'vue'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

const props = defineProps<{
  src: string
  alt?: string
  style?: any
  class?: string
}>()

const didError = ref(false)
const attrs = useAttrs()

const customClass = computed(() => props.class || attrs.class || '')
const restAttrs = computed(() => {
  // Remove class, style, src, alt from $attrs
  const { class: _class, style: _style, src: _src, alt: _alt, ...rest } = attrs
  return rest
})

function handleError() {
  didError.value = true
}
</script>
